mapper.select:
SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, TO_CHAR(MEMBER_BIRTH, 'YYYY-MM-DD') AS MEMBER_BIRTH, 
MEMBER_TEL, MEMBER_EMAIL, MEMBER_ADDRESS, MEMBER_PROFILE, MEMBER_STATE, WALK_STATE, MEMBER_GENDER, CREW_NAME AS MEMBER_CREWNAME
FROM MEMBER M LEFT OUTER JOIN CREW C ON M.MEMBER_CREWCODE = C.CREW_CODE WHERE M.MEMBER_ID = #{loginId}
